import './App.css';
let total = 0;
function App() {
  function handleClick(props) {
    let buttonText = props.target.value;
    let inputText = document.getElementById('result').value;
    console.log(inputText + buttonText);
    let total = inputText + buttonText;
    return total;
  }
  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
 }
  return (
    <div className="App">
          <div id="ans" className="answer">Ans = 0</div>
          <input type="text" value={console.log(total)}  placeholder="0" className="inputid" id="result"/>
           <hr/> 
           {/* calculator buttons below */}
           {/* button + */}
           <button className="opbut">+</button>
           {/* button - */}
           <button className="opbut">-</button>
           {/* button × */}
           <button className="opbut">×</button>
           {/* button ÷ */}
           <button className="opbut">÷</button>
           {/* button 0 */}
           <button className="calbut">0</button>
           {/* button 1 */}
           <input type="submit" value="1" onClick={handleClick} />
           <button className="calbut" value="1" label="1" onClick={handleClick}>1</button>
           {/* button 2 */}
           <button className="calbut"  value="2"  onClick={handleClick}>2</button>
           {/* button 3 */}
           <button className="calbut">3</button>
           {/* button 4 */}
           <button className="calbut">4</button>
           {/* button 5 */}
           <button className="calbut">5</button>
           {/* button 6 */}
           <button className="calbut">6</button>
           {/* button 7 */}
           <button className="calbut">7</button>
           {/* button 8 */}
           <button className="calbut">8</button>
           {/* button 9 */}
           <button className="calbut">9</button>
           {/* button ( */}
           <button className="opbut">(</button>
           {/* button ) */}
           <button className="opbut">)</button>
           {/* button % */}
           <button className="opbut">%</button>
           {/* button y^ */}
           <button className="opbut">y^</button>
           {/* button total = */}
           <button className="ansbut">=</button>
           <hr/>
           <div className="appName">A Simple React Calculator - Jayflo</div>
        </div>
  );
}

export default App;
