import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
class App22 extends React.Component{
  constructor(props) {
    super(props);
    this.state = {isToggleOn: true};
  }
  value =1;

  btn2 = () => {
    this.setState({btn: "2"});
  }

    // This binding is necessary to make `this` work in the callback    this.handleClick = this.handleClick.bind(this);  }
  render(){ return <div className="App">
  <div id="ans" className="answer">Ans = 0 </div>
  <input type="text" placeholder="0" className="inputid" id="result" value={value}  readOnly/>
   <hr/> 
   {/* calculator buttons below */}
   {/* button + */}
   <button className="opbut">+</button>
   {/* button - */}
   <button className="opbut">-</button>
   {/* button × */}
   <button className="opbut">×</button>
   {/* button ÷ */}
   <button className="opbut">÷</button>
   {/* button 0 */}
   <button className="calbut">0</button>
   {/* button 1 */}
   <button className="calbut">1</button>
   {/* button 2 */}
   <button className="calbut">2</button>
   {/* button 3 */}
   <button className="calbut">3</button>
   {/* button 4 */}
   <button className="calbut">4</button>
   {/* button 5 */}
   <button className="calbut">5</button>
   {/* button 6 */}
   <button className="calbut">6</button>
   {/* button 7 */}
   <button className="calbut">7</button>
   {/* button 8 */}
   <button className="calbut">8</button>
   {/* button 9 */}
   <button className="calbut">9</button>
   {/* button ( */}
   <button className="opbut">(</button>
   {/* button ) */}
   <button className="opbut">)</button>
   {/* button % */}
   <button className="opbut">%</button>
   {/* button y^ */}
   <button className="opbut">y^</button>
   {/* button total = */}
   <button className="ansbut">=</button>
   <hr/>
   <div className="appName">A Simple React Calculator - Jayflo</div>
</div>
}
}

ReactDOM.render(
  <React.StrictMode>
    <App22 />
  </React.StrictMode>,
  document.getElementById('root')
);

