import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
class App22 extends React.Component{
  constructor(props) {
    super(props);
    this.state = {isToggleOn: true};
  }
  calculate = () => {
    try {
        const result = eval(this.state.data);
        this.setState({data: result});
    } catch (e) {
        this.setState({data: 'error'})
    }
}

handleClick = e => {
    const value = e.target.getAttribute('data-value');
    switch(value) {
        case 'clear':
            this.setState({ data: ''});
            break;
        case 'equal':
            this.calculate();
            break;
        default:
            this.setState({ data: this.state.data + value});
    }
}

  render(){
      return (
        <div className="App">
          <div id="ans" className="answer">Ans = 0 </div>
          <input type="text" placeholder="0" className="inputid" id="result" />
           <hr/> 
           {/* calculator buttons below */}
           {/* button + */}
           <button className="opbut" onClick={this.handleClick}>+</button>
           {/* button - */}
           <button className="opbut">-</button>
           {/* button × */}
           <button className="opbut">×</button>
           {/* button ÷ */}
           <button className="opbut">÷</button>
           {/* button 0 */}
           <button className="calbut">0</button>
           {/* button 1 */}
           <button className="calbut">1</button>
           {/* button 2 */}
           <button className="calbut">2</button>
           {/* button 3 */}
           <button className="calbut">3</button>
           {/* button 4 */}
           <button className="calbut">4</button>
           {/* button 5 */}
           <button className="calbut">5</button>
           {/* button 6 */}
           <button className="calbut">6</button>
           {/* button 7 */}
           <button className="calbut">7</button>
           {/* button 8 */}
           <button className="calbut">8</button>
           {/* button 9 */}
           <button className="calbut">9</button>
           {/* button ( */}
           <button className="opbut">(</button>
           {/* button ) */}
           <button className="opbut">)</button>
           {/* button % */}
           <button className="opbut">%</button>
           {/* button y^ */}
           <button className="opbut">y^</button>
           {/* button total = */}
           <button className="ansbut">=</button>
           <hr/>
           <div className="appName">A Simple React Calculator - Jayflo</div>
        </div>
      );
    }
}

ReactDOM.render(
  <React.StrictMode>
    <App22 />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
