// import './Button.css';
import React from "react";

class BtnClicks extends React{
  constructor(props){
    super(props);
    this.state = {btn: ""};
  }
  ButtonClick = (event) => {
    return (
      this.setState({btn: event.target.value})
    );
  }
}


export default btn;
