import './App.css';

function App() {

    this.App2 = this.App2.bind(this);
  return (
    <div className="App">
      <div id="ans" className="answer">Ans = 0 </div>
      <input type="text" onChange={this.setState({myChangeHandler})}  placeholder="0" className="inputid" id="result" />
       <hr/> 
       {/* calculator buttons below */}
       {/* button + */}
       <button className="opbut" onClick={App2}>+</button>
       {/* button - */}
       <button className="opbut">-</button>
       {/* button × */}
       <button className="opbut">×</button>
       {/* button ÷ */}
       <button className="opbut">÷</button>
       {/* button 0 */}
       <button className="calbut">0</button>
       {/* button 1 */}
       <button className="calbut">1</button>
       {/* button 2 */}
       <button className="calbut">2</button>
       {/* button 3 */}
       <button className="calbut">3</button>
       {/* button 4 */}
       <button className="calbut">4</button>
       {/* button 5 */}
       <button className="calbut">5</button>
       {/* button 6 */}
       <button className="calbut">6</button>
       {/* button 7 */}
       <button className="calbut">7</button>
       {/* button 8 */}
       <button className="calbut">8</button>
       {/* button 9 */}
       <button className="calbut">9</button>
       {/* button ( */}
       <button className="opbut">(</button>
       {/* button ) */}
       <button className="opbut">)</button>
       {/* button % */}
       <button className="opbut">%</button>
       {/* button y^ */}
       <button className="opbut">y^</button>
       {/* button total = */}
       <button className="ansbut">=</button>
       <hr/>
       <div className="appName">A Simple React Calculator - Jayflo</div>
    </div>
  );
}
function App2(event) {
  return (
    this.setState({btn: event.target.value})
  );
}

function myChangeHandler(event) {
  let nam = event.target.name;
  let val = event.target.value;
  let err = '';
  if (nam === "age") {
    if (val !=="" && !Number(val)) {
      err = <strong>Your age must be a number</strong>;
    }  
  }
 this.setState({errormessage: err});
 this.setState({[nam]: val});
}

export default App;
