import './App.css';

function App() {
  function calculatorButtonClick(input) {
    let buttonText = input.target.innerText;
    let inputText = document.getElementById('result').value;
    let concatText = buttonText + inputText;
  }
  return (
    <div className="App">
          <div id="ans" className="answer">Ans = 0</div>
          <input type="text" placeholder="0" value={concatText} className="inputid" id="result"/>
           <hr/> 
           {/* calculator buttons below */}
           {/* button + */}
           <button className="opbut">+</button>
           {/* button - */}
           <button className="opbut">-</button>
           {/* button × */}
           <button className="opbut">×</button>
           {/* button ÷ */}
           <button className="opbut">÷</button>
           {/* button 0 */}
           <button className="calbut">0</button>
           {/* button 1 */}
           <button className="calbut" onClick={calculatorButtonClick({input:1})}>1</button>
           {/* button 2 */}
           <button className="calbut">2</button>
           {/* button 3 */}
           <button className="calbut">3</button>
           {/* button 4 */}
           <button className="calbut">4</button>
           {/* button 5 */}
           <button className="calbut">5</button>
           {/* button 6 */}
           <button className="calbut">6</button>
           {/* button 7 */}
           <button className="calbut">7</button>
           {/* button 8 */}
           <button className="calbut">8</button>
           {/* button 9 */}
           <button className="calbut">9</button>
           {/* button ( */}
           <button className="opbut">(</button>
           {/* button ) */}
           <button className="opbut">)</button>
           {/* button % */}
           <button className="opbut">%</button>
           {/* button y^ */}
           <button className="opbut">y^</button>
           {/* button total = */}
           <button className="ansbut">=</button>
           <hr/>
           <div className="appName">A Simple React Calculator - Jayflo</div>
        </div>
  );
}

export default App;
